mapper.select:
select 
from file_library
where id = #{id, jdbcType=VARCHAR}

mapper.update:
update file_library
set name = #{name, jdbcType=VARCHAR},
    update_user_id = #{updateUserId, jdbcType=VARCHAR},
    update_time    = #{updateTime, jdbcType=TIMESTAMP}
where id = #{id, jdbcType=VARCHAR}

mapper.delete:
delete
from file_library
where id = #{id, jdbcType=VARCHAR}
