mapper.delete:
delete
from file_library
where id = #{id, jdbcType=VARCHAR}

mapper.update:
update file_library
set space_id = null,
    update_user_id = #{updateUserId, jdbcType=VARCHAR}
where id in

    #{id, jdbcType=VARCHAR}

mapper.select:
select 
from file_library
where space_id = #{spaceId, jdbcType=VARCHAR}
  and create_user_id = #{userId, jdbcType=VARCHAR}
order by create_time desc
